    public class ClassroomsController : BaseController
    {
        private ClassroomBLO classroomBLO = new ClassroomBLO();

        public ActionResult Index()
        {
           return View(classroomBLO.FindAll());
        }

        public ActionResult Details(long? id)
        {
            if (id == null)
            {
                return new HttpStatusCodeResult(HttpStatusCode.BadRequest);
            }

            Classroom classroom = classroomBLO.FindBaseEntityByID((long) id);
            if (classroom == null)
            {
                return HttpNotFound();
            }
            return View(classroom);
        }

        public ActionResult Create()
        {
			 
            ViewBag.ClassroomCategoryId = new SelectList(new ClassroomCategoryBLO().FindAll(), "Id", "Code");
            return View();
        }

        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Create([Bind(Include = "Id,Code,Name,ClassroomCategoryId,Description")] Classroom classroom)
        {
            if (ModelState.IsValid)
            {
                classroomBLO.Save(classroom);

                return RedirectToAction("Index");
            }

            ViewBag.ClassroomCategoryId = new SelectList(new ClassroomCategoryBLO().FindAll(), "Id", "Code", classroom.ClassroomCategoryId);
            return View(classroom);
        }

        public ActionResult Edit(long? id)
        {
            if (id == null)
            {
                return new HttpStatusCodeResult(HttpStatusCode.BadRequest);
            }

            Classroom classroom = classroomBLO.FindBaseEntityByID((long)id);
            if (classroom == null)
            {
                return HttpNotFound();
            }
            ViewBag.ClassroomCategoryId = new SelectList(new ClassroomCategoryBLO().FindAll(), "Id", "Code", classroom.ClassroomCategoryId);
            return View(classroom);
        }

        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Edit([Bind(Include = "Id,Code,Name,ClassroomCategoryId,Description")] Classroom classroom)
        {
            if (ModelState.IsValid)
            {
                Classroom old_classroom = classroomBLO.FindBaseEntityByID(classroom.Id);
                UpdateModel(old_classroom);
                classroomBLO.Save(old_classroom);
                return RedirectToAction("Index");
            }
            ViewBag.ClassroomCategoryId = new SelectList(new ClassroomCategoryBLO().FindAll(), "Id", "Code", classroom.ClassroomCategoryId);
            return View(classroom);
        }

        public ActionResult Delete(long? id)
        {
            if (id == null)
            {
                return new HttpStatusCodeResult(HttpStatusCode.BadRequest);
            }

            Classroom classroom = classroomBLO.FindBaseEntityByID((long)id);
            if (classroom == null)
            {
                return HttpNotFound();
            }
