    // Generated by Manager v 0.0.3
    public class GroupsController : BaseController
    {
        private GroupBLO groupBLO = new GroupBLO();

        public ActionResult Index()
        {
           return View(groupBLO.FindAll());
        }

        public ActionResult Details(long? id)
        {
            if (id == null)
            {
                return new HttpStatusCodeResult(HttpStatusCode.BadRequest);
            }

            Group group = groupBLO.FindBaseEntityByID((long) id);
            if (group == null)
            {
                return HttpNotFound();
            }
            return View(group);
        }

        public ActionResult Create()
        {
			 
            ViewBag.SpecialtyId = new SelectList(new SpecialtyBLO().FindAll(), "Id", "Code");
			 
            ViewBag.TrainingTypeId = new SelectList(new TrainingTypeBLO().FindAll(), "Id", "Code");
			 
            ViewBag.TrainingYearId = new SelectList(new TrainingYearBLO().FindAll(), "Id", "Code");
            return View();
        }

        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Create([Bind(Include = "Id,TrainingTypeId,TrainingYearId,SpecialtyId,Year,Code,Description")] Group group)
        {
            if (ModelState.IsValid)
            {
                groupBLO.Save(group);
				Alert(string.Format(msgManager.The_Entity_was_well_created, msg_Group.SingularName, group), NotificationType.success);
                return RedirectToAction("Index");
            }

            ViewBag.SpecialtyId = new SelectList(new SpecialtyBLO().FindAll(), "Id", "Code", group.SpecialtyId);
            ViewBag.TrainingTypeId = new SelectList(new TrainingTypeBLO().FindAll(), "Id", "Code", group.TrainingTypeId);
            ViewBag.TrainingYearId = new SelectList(new TrainingYearBLO().FindAll(), "Id", "Code", group.TrainingYearId);
		    Alert(msgManager.The_information_you_have_entered_is_not_valid, NotificationType.warning);
            return View(group);
        }

        public ActionResult Edit(long? id)
        {
            if (id == null)
            {
                return new HttpStatusCodeResult(HttpStatusCode.BadRequest);
            }

            Group group = groupBLO.FindBaseEntityByID((long)id);
            if (group == null)
            {
                return HttpNotFound();
            }
            ViewBag.SpecialtyId = new SelectList(new SpecialtyBLO().FindAll(), "Id", "Code", group.SpecialtyId);
            ViewBag.TrainingTypeId = new SelectList(new TrainingTypeBLO().FindAll(), "Id", "Code", group.TrainingTypeId);
            ViewBag.TrainingYearId = new SelectList(new TrainingYearBLO().FindAll(), "Id", "Code", group.TrainingYearId);
            return View(group);
        }

        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Edit([Bind(Include = "Id,TrainingTypeId,TrainingYearId,SpecialtyId,Year,Code,Description")] Group group)
        {
            if (ModelState.IsValid)
            {
                Group old_group = groupBLO.FindBaseEntityByID(group.Id);
                UpdateModel(old_group);
                groupBLO.Save(old_group);
				Alert(string.Format(msgManager.The_entity_has_been_changed, msg_Group.SingularName, group), NotificationType.success);
                return RedirectToAction("Index");
            }
            ViewBag.SpecialtyId = new SelectList(new SpecialtyBLO().FindAll(), "Id", "Code", group.SpecialtyId);
            ViewBag.TrainingTypeId = new SelectList(new TrainingTypeBLO().FindAll(), "Id", "Code", group.TrainingTypeId);
            ViewBag.TrainingYearId = new SelectList(new TrainingYearBLO().FindAll(), "Id", "Code", group.TrainingYearId);

			Alert(msgManager.The_information_you_have_entered_is_not_valid, NotificationType.warning);
            return View(group);
        }

        public ActionResult Delete(long? id)
        {
            if (id == null)
            {
                return new HttpStatusCodeResult(HttpStatusCode.BadRequest);
            }

            Group group = groupBLO.FindBaseEntityByID((long)id);
            if (group == null)
            {
                return HttpNotFound();
            }
            return View(group);
        }

        [HttpPost, ActionName("Delete")]
        [ValidateAntiForgeryToken]
        public ActionResult DeleteConfirmed(long id)
        {
			Group group = groupBLO.FindBaseEntityByID((long)id);
            groupBLO.Delete(group);

			 

			Alert(string.Format(msgManager.The_entity_has_been_removed, msg_Group.SingularName, group), NotificationType.success);
            return RedirectToAction("Index");
        }

		public ActionResult Import()
        {
            //Save excel file to server
            HttpPostedFileBase parametersTemplate = Request.Files["import_objects"];

            // [Bug] if multiple user import the same file in the same moments
            string path = Server.MapPath("~/Content/Files/Upload" + parametersTemplate.FileName);
            if (System.IO.File.Exists(path))
            {
                System.IO.File.Delete(path);
                parametersTemplate.SaveAs(path);
            }
            parametersTemplate.SaveAs(path);

            //Save new parameters to database
            var excelData = new ExcelData(path); // link to other project
            DataTable firstTable = excelData.getFirstTable();

            try
            {
                string msg = groupBLO.Import(firstTable);
                Message(msg, NotificationType.info);
                return RedirectToAction("Index");
            }
            catch (ImportLineException e)
            {
                Message(e.Message, NotificationType.info);
              
            }
            return RedirectToAction("Index");
        }


        public FileResult Export()
        {
            using (XLWorkbook wb = new XLWorkbook())
            {
                DataTable dataTable = groupBLO.Export();
                wb.Worksheets.Add(dataTable);
                using (MemoryStream stream = new MemoryStream())
                {
                    wb.SaveAs(stream);
                    return File(stream.ToArray(), "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet", msg_Group.PluralName + ".xlsx");
                }
            }
        }

        protected override void Dispose(bool disposing)
        {
            if (disposing)
            {
                groupBLO.Dispose();
            }
            base.Dispose(disposing);
        }
    }
