        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Edit([Bind(Include = "Id,TrainingYearId,ModuleTrainingId,FormerId,GroupId,Name,Code,Description")] Training training)
        {
            if (ModelState.IsValid)
            {
                Training old_training = trainingBLO.FindBaseEntityByID(training.Id);
                UpdateModel(old_training);
                trainingBLO.Save(old_training);
				Alert(string.Format(msgManager.The_entity_has_been_changed, msg_Training.SingularName, training), NotificationType.success);
                return RedirectToAction("Index");
            }
            ViewBag.FormerId = new SelectList(new FormerBLO().FindAll(), "Id", "FirstName", training.FormerId);
            ViewBag.GroupId = new SelectList(new GroupBLO().FindAll(), "Id", "Code", training.GroupId);
            ViewBag.ModuleTrainingId = new SelectList(new ModuleTrainingBLO().FindAll(), "Id", "Name", training.ModuleTrainingId);
            ViewBag.TrainingYearId = new SelectList(new TrainingYearBLO().FindAll(), "Id", "Code", training.TrainingYearId);

			Alert(msgManager.The_information_you_have_entered_is_not_valid, NotificationType.warning);
            return View(training);
        }

